<template>
    <div id="app">
        <h1>Events</h1>
        <h1>{{ event_title }}</h1>
</template>

<script>
export default{
    name:'Events'
    props:['event_id'],
    computed:{
        event_title(){
            if(this.event_id=='1') {
                return '1+1!'
            } else if (this.event_id=='2') {
                return 'Free cookies'
            } else  
                return ''
        }
    }
};
</script>