import Vue from 'vue'
import VueRouter from 'vue-router'

import Home from '@/components/Home.vue'
import Menus from '@/components/Menus.vue'
import Events from '@/components/Events.vue'
import Reviews from '@/components/Reviews.vue'

Vue.use(VueRouter)


        // 주소별로 어떤 component를 할지는 router.js
        // 이후 셋팅은 main.js
        // App.vue에 export 는 빼고, router-view 작성 

export default new VueRouter({
    mode:'history',
    routes:[
        // localhost:8080/ 하면 Home.vue 띄워지는 식으로 , 객체 path,component를 넘겨준다. 이후
        // New.vue에 정보를 전달해야한다. main.js에 router정보를 전달해야 한다. import router from './router/router'
        // 이후 Vue에 router 추가  

        { path:'/', component:Home },
        { path:'/menus', component:Menus },
        { path:'/menus/:menu:id', component:Menus},
        { path:'/events', component:Events, props:(route)=>({ query_id:route.query.event_id }) },
        { path:'/events/:event_id', component:Events, props:true},
        { path:'/reviews', component:Reviews }
    ]
});

// /menus => Menus.vue  메뉴 리스트가 출력 , 클릭하면 상세 화면
// /menus/1 => MenuItem.vue 메뉴 상세 화면